mapper.select:
SELECT siteno, img, imgsaved, thumb, imgsize, kor_co_nm, homp_url, cal_tel, cnt, r FROM ( SELECT siteno, img, imgsaved, thumb, imgsize, kor_co_nm, homp_url, cal_tel, cnt, rownum as r FROM ( SELECT siteno, img, imgsaved, thumb, imgsize, kor_co_nm, homp_url, cal_tel, cnt FROM site
WHERE sitename LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%'
ORDER BY siteno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT siteno, img, imgsaved, thumb, imgsize, kor_co_nm, homp_url, cal_tel, cnt
FROM site
WHERE siteno=#{siteno}

mapper.update:
UPDATE site
SET img=#{img}, imgsaved=#{imgsaved}, thumb=#{thumb}, imgsize=#{imgsize},
      kor_co_nm=#{kor_co_nm}, homp_url=#{homp_url}, cal_tel=#{cal_tel}
WHERE siteno=#{siteno}
