mapper.select:
SELECT siteno, img, imgsaved, thumb, imgsize, sitename, surl, word, cnt, r FROM ( SELECT siteno, img, imgsaved, thumb, imgsize, sitename, surl, word, cnt, rownum as r FROM ( SELECT siteno, img, imgsaved, thumb, imgsize, sitename, surl, word, cnt FROM site
WHERE sitename LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%'
ORDER BY siteno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT siteno, img, imgsaved, thumb, imgsize, sitename, surl, word, cnt
FROM site
WHERE siteno=#{siteno}

mapper.update:
UPDATE site
SET img=#{img}, imgsaved=#{imgsaved}, thumb=#{thumb}, imgsize=#{imgsize},
      sitename=#{sitename}, surl=#{surl}, word=#{word}
WHERE siteno=#{siteno}
