mapper.select:
SELECT depositno, kor_co_nm, fin_prdt_nm, join_way, mtrt_int, r FROM ( SELECT depositno, kor_co_nm, fin_prdt_nm, join_way, mtrt_int, rownum as r FROM ( SELECT depositno, kor_co_nm, fin_prdt_nm, join_way, mtrt_int FROM deposit_data
WHERE kor_co_nm LIKE '%' || #{word} || '%' OR fin_prdt_nm LIKE '%' || #{word} || '%'
ORDER BY depositno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT depositno, kor_co_nm, fin_prdt_nm, join_way, mtrt_int
FROM deposit_data
WHERE depositno=#{depositno}

mapper.update:
UPDATE deposit_data
SET kor_co_nm=#{kor_co_nm}, fin_prdt_nm=#{fin_prdt_nm}, join_way=#{join_way}, mtrt_int=#{mtrt_int}
WHERE depositno=#{depositno}
