mapper.select:
SELECT noticeno, categrpno, memberno, title, content, file1, file1saved, thumb1, size1, cnt, replycnt, rdate
FROM notice
WHERE noticeno=#{noticeno}

mapper.update:
UPDATE notice
SET title=#{title}, content=#{content}, file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE noticeno=#{noticeno}
